       host: 'jdbc:mariadb://185.47.185.47/',
       user: 'dembr80',
       password: 'scibola80',
       database: 'elszamolas'
});
connection.connect((err) => {
  if (err) throw err;
  console.log('Connected!');
});
}  


      
        render() {
            return ( 
              < button onClick = {  this.ConnectDB } > 
                Click
              </button>)
            }

        }

